mapper.select:
select t1.user_id as userId,
t1.is_valid as isValid,
t1.user_birtd as userBirth,
t1.user_head as userHead,
t1.user_name as userName,
t1.user_sex as userSex,
t2.login_name as loginName,
t2.`password` as `password`,
t2.phone as phone,
t2.remark as remark,
t2.salt as salt
from t_admin_user t1 left JOIN t_admin_user_login t2 on t1.user_id =
t2.user_id
where 1=1

and t1.user_id = #{userId,jdbcType=VARCHAR}


and t1.is_valid = #{isValid,jdbcType=VARCHAR}


and t1.user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')


and t1.user_sex = #{userSex,jdbcType=VARCHAR}


and t2.login_name = #{loginName,jdbcType=VARCHAR}


and t2.phone = #{phone,jdbcType=VARCHAR}

select t1.user_id as userId, t1.is_valid as isValid, t1.user_birtd as userBirth, t1.user_head as userHead, t1.user_name as userName, t1.user_sex as userSex, t2.login_name as loginName, t2.`password` as `password`, t2.phone as phone, t2.remark as remark, t2.salt as salt from t_admin_user t1 left JOIN t_admin_user_login t2 on t1.user_id = t2.user_id where 1=1
and t1.user_id = #{userId,jdbcType=VARCHAR}
and t1.is_valid = #{isValid,jdbcType=VARCHAR}
and t1.user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')
and t1.user_sex = #{userSex,jdbcType=VARCHAR}
and t2.login_name = #{loginName,jdbcType=VARCHAR}
and t2.phone = #{phone,jdbcType=VARCHAR}
and t1.user_id in (select t3.user_id from t_admin_dep_user t3 where t3.dep_id = #{depId,jdbcType=VARCHAR} and t3.is_valid='Y')
and t1.user_id in (select t3.user_id from t_admin_department t4 left join t_admin_dep_user t3 on t3.dep_id =t4.dep_id where t4.path_code like concat(#{pathCode,jdbcType=VARCHAR},'%'}) and t3.is_valid='Y')
